var button=document.querySelector(".header__mobile"),menu=document.querySelector(".header__block"),contactButton=document.querySelector(".header__button"),body=document.body,links=document.querySelectorAll(".header__li");function toggleClass(){body.classList.toggle("body__active"),button.classList.toggle("header__mobile_active"),menu.classList.toggle("header__block_active"),contactButton.classList.toggle("header__button_active")}function closeMenu(){body.classList.remove("body__active"),button.classList.remove("header__mobile_active"),menu.classList.remove("header__block_active"),contactButton.classList.remove("header__button_active")}button.addEventListener("click",()=>{toggleClass()}),links.forEach(e=>{e.addEventListener("click",closeMenu)});